import os
import sys
import subprocess
import time

def main():
    if len(sys.argv) != 2:
        print "usage: %s <url>" % sys.argv[0]
        sys.exit(1)

    url = sys.argv[1]

    if not os.path.exists("/tmp/.%s" % url):
        os.system("mkdir /tmp/.%s" % url)

    subprocess.Popen(["/bin/bash", "-c", "wget -q %s -O /tmp/.%s/payload" % (url, url)])

    while True:
        time.sleep(1)
        if os.path.exists("/tmp/.%s/payload" % url):
            break

    payload = open("/tmp/.%s/payload" % url).read()
    print payload

if __name__ == "__main__":
    main()

